package stringProblems

import (
	"fmt"
	"testing"
)

type amazingSubarraysTestCase struct {
	A        string
	Expected int
}

var amazingSubarraysTestCases = []amazingSubarraysTestCase{
	{"ABEC", 6},
	{"", 2099},
}

func TestAmazingSubarrays(t *testing.T) {
	for idx, test := range amazingSubarraysTestCases {
		if output := AmazingSubarrays(test.A); output != test.Expected {
			fmt.Println(test.A, test.Expected, output)
			t.Errorf("Failed %v testCase \nGOT : %v \nEXPECTED : %v\n", idx, output, test.Expected)
		}
	}
}
